import React, {useState, useEffect} from 'react';
import './Header.css'
import Box from '@mui/material/Box';
import Tab from '@mui/material/Tab';
import TabContext from '@mui/lab/TabContext';
import TabList from '@mui/lab/TabList';
import TabPanel from '@mui/lab/TabPanel';
import { withStyles } from '@mui/styles';
import HomeIcon from '@mui/icons-material/Home';


import DigitalCreations from './DigitalCreations';
import EventPlanning from './EventPlanning';
import Photography from './Photography';
import WritingSample from './WritingSample'

const Header = () => { 
    const [value, setValue] = React.useState('1');
    const [re, setRe] = React.useState('');
    const [redirectPage, setRedirectPage] = React.useState(false);

    const handleChange = (event, newValue) => {
      setValue(newValue);
    };

    const styles = theme => ({
        indicator: {
          backgroundColor: 'white',
          color: '#71CECE'
        },
      })


      const onClick = (letsSetRedirect) => {
          setRe(letsSetRedirect);
          setRedirectPage(true);
      }


    return (
    <div>
        <div className="header">
            <h1>
            Meghan Levinson
            </h1>
            <div className="subHeader">
                Digital Marketer & Content Creator 
            </div>
            <HomeIcon className="home" style={{fontSize: '4vh', paddingBottom: '10vh'}}/>
        </div>

        <div className="tab">
        <Box sx={{ width: '100%' }} >
        <TabContext value={value} >
            <TabList onChange={handleChange}>
                <Tab label="Writing Sample" value="1" onClick={()=>onClick('WritingSample')} style={{ color: '#71CECE', borderBottom: '#71CECE'}} />
                <Tab label="Event Planning" value="2"  onClick={()=>onClick('EventPlanning')} style={{ color: '#71CECE', borderBottom: '#71CECE'}} />
                <Tab label="Digital Creations" value="3" onClick={()=>onClick('DigitalCreations')}  style={{ color: '#71CECE', borderBottom: '#71CECE'}}  />
                <Tab label="Photography" value="4" onClick={()=>onClick('Photography')} style={{ color: '#71CECE', borderBottom: '#71CECE'}} />
            </TabList>
        </TabContext>
        </Box>
        </div>
        {redirectPage ? <Navigate to={{pathname: `/${re}`}} />:''}
    </div>
)
}
export default Header
